import React, { useState, useEffect } from 'react';
import Login from './components/Login';
import Input from './components/Input';
import SpotifyWebApi from 'spotify-web-api-node';

//const SpotifyWebApi = require('spotify-web-api-node');

// credentials are optional
export const spotifyApi = new SpotifyWebApi({
	clientId: '8e202c414c364b5992a89b2fa4213ab8',
	clientSecret: 'dc426ebf7c894eaaad0701540618eeac',
	redirectUri: 'https://oauth.pstmn.io/v1/browser-callback',
	refresh_token: 'https://accounts.spotify.com/api/token',
});
const App = () => {
	const [isConnected, setIsConnected] = useState(false);

	useEffect(() => {
		const hash = window.location.hash;
		let tokenSet;
		if (hash) {
			tokenSet = hash
				.substring(1)
				.split('&')
				.find((elem) => elem.startsWith('access_token'))
				.split('=')[1];
			window.location.hash = '';
		}

		if (tokenSet === undefined || tokenSet === '' || tokenSet === null) return;
		spotifyApi.setAccessToken(tokenSet);
		setIsConnected(true);
	}, []);
	if (isConnected === true) {
		console.log('true');
	} else {
		console.log('false');
	}
	function logOut() {
		setIsConnected(false);
		window.location.reload();
	}
	return (
		<div>
			<Login />
			<Input />
		</div>
	);
};

export default App;
