import React from "react";

const Result = (props) => {
  console.log(props);
  return (
    <div className="result">
      <h1>RESULTS</h1>
      <p>the loan amount: {props.amount}</p>
      <p>Months to repay: {props.term}</p>
      <p>Total: {props.total}</p>
      <p>monthly payment: {props.total / props.term}</p>
    </div>
  );
};

export default Result;
