import React, { Component, Fragment } from "react";
import "./App.css";
import Form from "./components/Form";
import { CalculateTotal } from "./helper";
import Result from "./components/result";
import Message from "./components/Message";

class App extends Component {
  state = {
    total: "",
    amount: "",
    term: "",
  };
  loanInformation = (amount, term) => {
    const total = CalculateTotal(amount, term);
    this.setState({
      amount,
      term,
      total,
    });
  };
  render() {
    const { total, term, amount } = this.state;
    let component;
    if(total === ''){
      component = <Message />
    }else{
      component =  <Result total={total} term={term} amount={amount} />
    }
    return (
      <Fragment>
        <h1>Loan caculatator</h1>
        <div className="container">
          <Form loanInformation={this.loanInformation} />
        <div className="message">

        </div>


        </div>
      </Fragment>
    );
  }
}
export default App;
