import React, { Component, Fragment } from "react";
class Message extends Component {
  state = {
    nam: "",
    mail: "",
    part: "",
  };
  handleSubmit = (e) => {
    e.preventDefault();
    const { nam, mail, part } = this.state;
  };
  handleChange = (e) => {
    const { nam, mail, part } = e.target;
    this.setState({
      [name]: (nam),
    });
  }
  render() {
    const { nam, mail, part } = this.state;
    return (
      <Fragment>
        <form className="container" onSubmit={this.handleSubmit}>
          <div className="container1">
            <h1>NAME</h1>
            <form name="nam" className="form">
              <label className="label">Name</label>
              <input className="amount" onChange={this.handleChange}></input>
            </form>
          </div>
          <div className="container2">
            <h1>EMAIL</h1>
            <form name="mail" className="form">
              <label className="label">Email</label>
              <input className="amount"></input>
            </form>
          </div>
          <div className="container3">
            <h1>DEPARTMENT</h1>
            <form name="part" className="form">
              <label className="label">DEPARTMENT</label>
              <input className="amount"></input>
            </form>
          </div>
        </form>
        <a href="#" className="but" type="submit">
          Create
        </a>
      </Fragment>
    );
  }
}

export default Message;
